import express from 'express';
import isAuth from '../middlewares/isAuth.js';
import { createNewChat, getAllChat, getMessageByChat, sendMessage } from '../controllers/chat.js';
import upload from '../middlewares/multet.js';
const router = express.Router();
router.post('/chat/new', isAuth, createNewChat);
router.get('/chat/all', isAuth, getAllChat);
router.post('/message', isAuth, upload.single('image'), sendMessage);
router.get('/message/:chatId', isAuth, getMessageByChat);
export default router;
